
public class Ejercicio3App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int X = 10, Y = 20;
		double N = 11.5, M = 25.25;
		
		System.out.println("X = "+X+", Y = "+Y+", N = "+N+", M = "+M+"\n");
		
		//X y Y
		
		System.out.println("Suma de X + Y= "+(X+Y));
		
		System.out.println("Resta de X - Y= "+(X-Y));
		
		System.out.println("Producto de X * Y= "+(X*Y));
		
		System.out.println("Cociente de X / Y= "+(X/Y));
		
		System.out.println("Resto de X / Y= "+(X%Y)+"\n");
		
		//N y M
		
		System.out.println("Suma de N + M= "+(N+M));
		
		System.out.println("Resta de N - M= "+(N-M));
		
		System.out.println("Producto de N * M= "+(N*M));
		
		System.out.println("Cociente de N / M= "+(N/M));
		
		System.out.println("Resto de N / M= "+(N%M)+"\n");
		
		
		//N, M, X, Y
		
		System.out.println("Suma de X + N="+(X + N));
		
		System.out.println("Cociente de Y / M="+(X / N));
		
		System.out.println("Resto de Y % M="+(X / N)+"\n");
		
		System.out.println("El doble de X = "+(X*2)+" El doble de Y = "+(Y*2)+" El doble de N="+(N*2)+" El doble de M="+(M*2));
		
		
		
		
		
		
	}

}
